angular.module("miaomiao.console.directives",[]).directive("backImg",function(){return function(scope,element,attrs){var url=attrs.backImg;element.css({"background-image":"url("+url+")","background-size":"contain"});};}).directive("ngTimeSelector",function(){return{restrict:"EA",templateUrl:"templates/timepicker.html",scope:{hours:"=",minutes:"="},replace:true,link:function(scope,elem,attr){scope.period="上午";scope.increaseHours=function(){if(scope.hours<23){scope.hours=++scope.hours;}else{scope.hours=0;}};scope.decreaseHours=function(){scope.hours=scope.hours<=0?23:--scope.hours;};scope.increaseMinutes=function(){if(scope.minutes>=59){scope.minutes=0;}else{scope.minutes++;}};scope.decreaseMinutes=function(){if(scope.minutes<=0){scope.minutes=59;}else{scope.minutes=--scope.minutes;}};scope.displayHours=function(){var hoursToDisplay=scope.hours;if(scope.hours>12){hoursToDisplay=scope.hours-12;}if(hoursToDisplay==0){hoursToDisplay=12;}else{if(hoursToDisplay<=9){hoursToDisplay="0"+hoursToDisplay;}}return hoursToDisplay;};scope.displayMinutes=function(){return scope.minutes<=9?"0"+scope.minutes:scope.minutes;};scope.switchPeriod=function(){scope.hours=scope.hours>=12?scope.hours-12:scope.hours+12;};}};});