angular.module("miaomiao.shop").controller("ShoppingCartCtrl",function(a,d,f,b,c,e){a.info=a.info||{};a.info.shoppingCartItems=e.getAllItems();a.selectItem=function(g){g.count+=1;e.itemChangeEventInShoppingCart(g);};a.removeItem=function(h,g){h.count-=1;if(h.count<=0){h.count=0;e.removeItemFromCart(h);}e.itemChangeEventInShoppingCart(h);};a.clearShoppingCart=function(){a.info.shoppingCartItems=[];e.clearAll();};e.onItemChangeEventInProductList(a,function(g){a.info.shoppingCartItems=e.getAllItems();});});